- \"PHQ-9 total\"","# Hmisc::label(nhanes1$smoker) \u003c- \"Smoking status\"","# Hmisc::label(nhanes1)","","# Drop some extra variables","nhanes \u003c- nhanes1 %\u003e%","  select(-INDHHIN2, -starts_with(\"BPXSY\"), -starts_with(\"BPXDI\"), -SMQ020, -SMQ040, -PEASCCT1, -ALQ111, -ALQ121)","","dim(nhanes1) # 9254  102","dim(nhanes)  # 9254   90","summary(nhanes)","","Hmisc::describe(nhanes)","","#---","# Create 2 subsamples of 1000 adults for unweighted analyses ####","#---","# (I want a subsample so not all the p-values are super small! Just for illustration and teaching.)","","# nhanes_adult_int \u003c- nhanes %\u003e% ","#   filter(RIDAGEYR \u003e= 20) ","# dim(nhanes_adult_int) # 5569  116","","nhanes_adult_exam \u003c- nhanes %\u003e% ","  filter(RIDAGEYR \u003e= 20 \u0026 WTMEC2YR \u003e 0)","dim(nhanes_adult_exam) # 5265  90","# About the same. Just use the examination dataset","","# Separate fasting dataset. Take larger subsample. ","nhanes_adult_fast \u003c- nhanes %\u003e% ","  filter(RIDAGEYR \u003e= 20 \u0026 WTSAF2YR \u003e 0) ","dim(nhanes_adult_fast) # 2295  90","","# Use the weights to subsample so it is at least approximately representative","# (to do an actual weighted analysis, though, do NOT use this dataset!)","","# Sample WITH replacement... that will help us get closer to the population distribution","# (especially in in the fasting subsample which is only N = 2295 to begin with)","","# Set seed so same results every time","set.seed(354026)","SUB \u003c- sample(x    = 1:nrow(nhanes_adult_exam),","              size = 1000,","              prob = nhanes_adult_exam$WTMEC2YR/sum(nhanes_adult_exam$WTMEC2YR),","              replace = T)","nhanes_adult_exam_sub \u003c- nhanes_adult_exam[SUB, ]","nrow(nhanes_adult_exam)","nrow(nhanes_adult_exam_sub)","","set.seed(409875)","SUB \u003c- sample(x    = 1:nrow(nhanes_adult_fast),","              size = 1000,","              prob = nhanes_adult_fast$WTSAF2YR/sum(nhanes_adult_fast$WTSAF2YR),","            