ates.html","# 2017","# White Non-Hispanic: 60.6 percent","# Black Non-Hispanic: 12.3 percent","# Asian Non-Hispanic: 5.5 percent","# American Indian/Alaska Native Non-Hispanic: 0.7 percent","# Separately, Hispanics of any race accounted for 18.1 percent of the U.S. population.","","# This works to make the unweighted analyses a little more realistic.","","#---","# Create a character variable to illustrate how to convert to a factor ####","#---","nhanes_adult_fast_sub$cat_not_factor \u003c- c(rep(1, 100), rep(2, 700), rep(3, 200))","is.factor(nhanes_adult_fast_sub$cat_not_factor)","","#---","# Save final datasets ####","#---","","# Full dataset","save(nhanes,                file = \"nhanes1718_rmph.Rdata\")","","# Subsamples","","nhanes_adult_exam_sub \u003c- nhanes_adult_exam_sub %\u003e% ","  select(-SEQN, -WTINT2YR, -WTMEC2YR, -SDMVPSU, -SDMVSTRA)","","nhanes_adult_fast_sub \u003c- nhanes_adult_fast_sub %\u003e% ","  select(-SEQN, -WTINT2YR, -WTMEC2YR, -SDMVPSU, -SDMVSTRA)","","save(nhanes_adult_exam_sub, file = \"nhanes1718_adult_exam_sub_rmph.Rdata\")","save(nhanes_adult_fast_sub, file = \"nhanes1718_adult_fast_sub_rmph.Rdata\")","","#---","# Summarize data ####","#---","# load(\"nhanes1718_rmph.Rdata\")","# load(\"nhanes1718_adult_exam_sub_rmph.Rdata\")","# load(\"nhanes1718_adult_fast_sub_rmph.Rdata\")","","write(Hmisc::html(Hmisc::describe(nhanes)), \"nhanes1718_rmph.html\")","write(Hmisc::html(Hmisc::describe(nhanes_adult_exam_sub)), \"nhanes1718_adult_exam_sub_rmph.html\")","write(Hmisc::html(Hmisc::describe(nhanes_adult_fast_sub)), \"nhanes1718_adult_fast_sub_rmph.html\")","","#---","# Create matched case-control data example ####","#---","","load(\"nhanes1718_adult_exam_sub_rmph.Rdata\")","library(tidyverse)","","# https://cran.r-project.org/web/packages/ccoptimalmatch/vignettes/ccoptimalmatching_vignette.html","table(nhanes_adult_exam_sub$MCQ220, exclude = NULL)","","nhanes_complete \u003c- nhanes_adult_exam_sub %\u003e% ","  select(MCQ220, smoker, RIAGENDR, income) %\u003e% ","  drop_na()","nrow(nhanes_complete)","","create_subset \u003c- nhanes_complete %\u003e%","  filter(MCQ220 == \"Yes\") %\u003e%","  arrange(RIAGENDR, income) %\u003e%","  distinct(RIAGENDR, income, .keep_all = TRUE) %\u003e%","  mutate(subset = 1:n()) %\u003e%","  select(RIAGENDR, income, subset)","","nrow(create_subset)","# Unique combinations of gender and income","table(create_subset$RIAGENDR, create_subset$income, exclude = NULL)","","# Merge to create case dataset","# Keep all rows from both create_subset and cases","case_with_subset \u003c- nhanes_complete %\u003e%","  filter(MCQ220 == \"Yes\") %\u003e%","  full_join(create_subset, by = c(\"RIAGENDR\", \"income\")) %\u003e% ","  mutate(status = \"Case\")","","# All the cases","nrow(case_with_subset)","","# Merge to create control dataset","# Keep all controls that match","control_with_subset \u003c- nhanes_complete %\u003e%","  filter(MCQ220 == \"No\") %\u003e%","  right_join(create_subset, by = c(\"RIAGENDR\", \"income\")) %\u003e% ","  mutate(status = \"Control\")","","# Controls match on age and education","nrow(control_with_subset)","","CCdat \u003c- rbind(case_with_subset, control_with_subset)","","table(CCdat$status, exclude = NULL)","","table(CCdat$subset, CCdat$MCQ220, exclude = NULL)","table(CCdat$MCQ220, exclude = NULL)","","# OLD - The following was not needed for this dataset","# # Missing value for MCQ220 if no matching control.","# CCdat %\u003e%","#   filter(is.na(MCQ220)) %\u003e% ","#   select(subset, MCQ220, RIAGENDR, income)","# ","# CCdat %\u003e%","#   filter(subset == 38) %\u003e% ","#   select(subset, MCQ220, RIAGENDR, income)","# ","# NoMatch \u003c- CCdat %\u003e% ","#   filter(is.na(MCQ220)) %\u003e% ","#   select(subset)","# nrow(NoMatch)","# ","# CCdat \u003c- CCdat %\u003e% ","#   anti_join(NoMatch)","# nrow(CCdat)","# table(CCdat$MCQ220, exclude = NULL)","","levels(CCdat$MCQ220)","","nhanes_CC \u003c- CCdat","","summary(nhanes_CC)","","save(nhanes_CC, file = \"nhanes_CC_rmph.Rdata\")","","#---","# Create small n dataset for text example ####","#---","","library(tidyverse)","load(\"nhanes1718_adult_fast_sub_rmph.Rdata\")","nhanesf \u003c- nhanes_adult_fast_sub","rm(nhanes_adult_fast_sub)","","nhanesf.complete \u003c- nhanesf %\u003e% ","  select(LBDGLUSI, BMXWAIST, smoker, RIDAGEYR,","         RIAGENDR, RIDRETH3, income) %\u003e% ","  drop_na() %\u003e% ","  mutate(race_eth = fct_collapse(RIDRETH3,","                                 \"Hispanic\"           = c(\"Mexican American\", \"Other Hispanic\"),","                                 \"Non-Hispanic Other\" = c(\"Non-Hispanic Asian\", \"Other/Multi\")))","","nhanesf.complete$race_ethb \u003c- nhanesf.complete$race_eth","nhanesf.complete$incomeb \u003c- nhanesf.complete$income","","nhanesf.complete \u003c- nhanesf.complete %\u003e% ","  mutate(cRIDAGEYR = RIDAGEYR - 40,","         cBMXWAIST = BMXWAIST - 100)","","set.seed(345823)","","# Randomly select 50 rows from the dataset","ROWS.SUBSET \u003c- sample(1:nrow(nhanesf.complete), 50)","nhanesf.complete.50 \u003c- nhanesf.complete[ROWS.SUBSET,]","nrow(nhanesf.complete.50)","","save(nhanesf.complete.50, file = \"nhanesf.complete.50_rmph.Rdata\")","","","#---","# Create small n dataset for exercise ####","#---","","library(tidyverse)","load(\"nhanes1718_adult_fast_sub_rmph.Rdata\")","nhanesf \u003c- nhanes_adult_fast_sub","rm(nhanes_adult_fast_sub)","","nhanesf.complete \u003c- nhanesf %\u003e% ","  select(LBDTRSI, DXDTRPF, RIDAGEYR, RIAGENDR) %\u003e% ","  drop_na()","","set.seed(53247)","","# Randomly select 30 rows from the dataset","ROWS.SUBSET \u003c- sample(1:nrow(nhanesf.complete), 30)","nhanesf.complete.30 \u003c- nhanesf.complete[ROWS.SUBSET,]","nrow(nhanesf.complete.30)","","save(nhanesf.complete.30, file = \"nhanesf.complete.30_rmph.Rdata\")"],"stylingDirectives":null,"colorizedLines":["\u003cspan class=\"pl-c\"\u003e\u003cspan class=\"pl-c\"\u003e#\u003c/span\u003e NOTE: This code is provided solely to create example datasets used in Introduction to Regression Methods for Public Health using R\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e\u003cspan class=\"pl-c\"\u003e#\u003c/span\u003e       No warranty is provided regarding the correctness of this code or regarding any analyses carried out with the\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e\u003cspan class=\"pl-c\"\u003e#\u003c/span\u003e       datasets produced by this code.\u003c/span\u003e","","\u003cspan class=\"pl-c\"\u003e\u003cspan class=\"pl-c\"\u003e#\u003c/span\u003e---\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e\u003cspan class=\"pl-c\"\u003e#\u003c/span\u003e Program Name:    NHANES 2017 2018 Process.R\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e\u003cspan class=\"pl-c\"\u003e#\u003c/span\u003e Analyst:         Ramzi W. Nahhas\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e\u003cspan class=\"pl-c\"\u003e#\u003c/span\u003e Date:            September 1, 2021\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e\u003cspan class=\"pl-c\"\u003e#\u003c/span\u003e Updated:         March 11, 2024\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e\u003cspan class=\"pl-c\"\u003e#\u003c/span\u003e Contents:        Download and process NHANES data to create example datasets\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e\u003cspan class=\"pl-c\"\u003e#\u003c/span\u003e---\u003c/span\u003e","","\u003cspan class=\"pl-c\"\u003e\u003cspan class=\"pl-c\"\u003e#\u003c/span\u003e install.packages(\u0026quot;tidyverse\u0026quot;, \u0026quot;nhanesA\u0026quot;, \u0026quot;Hmisc\u0026quot;, \u0026quot;survey\u0026quot;)\u003c/span\u003e","library(\u003cspan class=\"pl-smi\"\u003etidyverse\u003c/span\u003e)","library(\u003cspan class=\"pl-smi\"\u003enhanesA\u003c/span\u003e) \u003cspan class=\"pl-c\"\u003e\u003cspan class=\"pl-c\"\u003e#\u003c/span\u003e To import NHANES files\u003c/span\u003e","","\u003cspan class=\"pl-c\"\u003e\u003cspan class=\"pl-c\"\u003e#\u003c/span\u003e Just to save typing\u003c/span\u003e","\u003cspan class=\"pl-en\"\u003emytab\u003c/span\u003e  \u003cspan class=\"pl-k\"\u003e\u0026lt;-\u003c/span\u003e \u003cspan class=\"pl-k\"\u003efunction\u003c/span\u003e(\u003cspan class=\"pl-smi\"\u003ex\u003c/span\u003e) table(\u003cspan class=\"pl-smi\"\u003ex\u003c/span\u003e, \u003cspan class=\"pl-v\"\u003eexclude\u003c/span\u003e \u003cspan class=\"pl-k\"\u003e=\u003c/span\u003e \u003cspan class=\"pl-c1\"\u003eNULL\u003c/span\u003e)","\u003cspan class=\"pl-en\"\u003emyxtab\u003c/span\u003e \u003cspan class=\"pl-k\"\u003e\u0026lt;-\u003c/span\u003e \u003cspan class=\"pl-k\"\u003efunction\u003c/span\u003e(\u003cspan class=\"pl-smi\"\u003ex1\u003c/span\u003e, \u003cspan class=\"pl-smi\"\u003ex2\u003c/span\u003e) table(\u003cspan class=\"pl-smi\"\u003ex1\u003c/span\u003e, \u003cspan class=\"pl-smi\"\u003ex2\u003c/span\u003e, \u003cspan class=\"pl-v\"\u003eexclude\u003c/span\u003e \u003cspan class=\"pl-k\"\u003e=\u003c/span\u003e \u003cspan class=\"pl-c1\"\u003eNULL\u003c/span\u003e)","","\u003cspan class=\"pl-c\"\u003e\u003cspan class=\"pl-c\"\u003e#\u003c/span\u003e---\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e\u003cspan class=\"pl-c\"\u003e#\u003c/span\u003e Load NHANES 2017-2018 data ####\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e\u003cspan class=\"pl-c\"\u003e#\u003c/span\u003e---\u003c/span\u003e","","\u003cspan class=\"pl-c\"\u003e\u003cspan class=\"pl-c\"\u003e#\u003c/span\u003e Use SI units!\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e\u003cspan class=\"pl-c\"\u003e#\u003c/span\u003e Conversions from conventional to SI are not simple. https://www.ncbi.nlm.nih.gov/books/NBK33478/\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e\u003cspan class=\"pl-c\"\u003e#\u003c/span\u003e Just use variables that have SI versions.\u003c/span\u003e","","\u003cspan class=\"pl-c\"\u003e\u003cspan class=\"pl-c\"\u003e#\u003c/span\u003e summary(hdl$LBDHDDSI / hdl$LBDHDD) # 0.02586 according to https://www.ncbi.nlm.nih.gov/books/NBK33